import React, {useState} from "react";
import { Link } from "react-router-dom";
import Clouds from "./Clouds";
import 'animate.css';

const LandingPage = () => {

  const [isHover, setIsHover] = useState(false);
  return (
  <div className="text-hot-red max-h-screen absolute inset-0 flex flex-col items-center justify-center gap-10">
    <Clouds numberOfClouds={10}/>
    {/* <Link to="/" className="menu-item"> */}
    <h1 className={`text-6xl text-sm:text-4xl ${isHover ? "menu-item" : ""}`} onMouseEnter={() => setIsHover(true)} onMouseLeave={() => setIsHover(false)}>
    Nika Darab
</h1>
    {/* </Link> */}
    <h2 className="text-4xl text-sm:text-2xl">Software Engineer</h2>
    <h2 className="text-4xl text-sm:text-2xl">Story Teller</h2>
  </div>
)};
export default LandingPage;
