export const tabControl = (
  tab,
  setIsHome,
  setIsAbout,
  setIsProjects,
  setContent
) => {
  if (tab === "home") {
    setIsHome(true);
    setIsAbout(false);
    setIsProjects(false);
    setContent(
      <div className="flex flex-col not-fancy-font  responsive-text w-full">
        <div className="text-hot-pink text-50">HOME</div>
        <div className="line-1">Hi, I'm Nika!</div>
        <div className="line-2">
          a software engineer and an artist based in Dallas.
        </div>
        <div className="line-3">let's create, collaborate, and connect!</div>
      </div>
    );
    return;
  }
  if (tab === "about") {
    setIsHome(false);
    setIsAbout(true);
    setIsProjects(false);
    setContent(
      <div className="flex flex-col not-fancy-font  responsive-text mb-10 w-full mr-20">
        <h5 className="text-hot-pink">ABOUT</h5>
        <div className="line-1 mb-10">
          I am a highly experienced software engineer with a passion for
          creating innovative and user-friendly digital solutions. With a
          background in Javascript, HTML5, CSS3, Node.js, and Ruby, I am
          well-versed in front-end development and an expert in frameworks such
          as React.
        </div>
        <div className="line-2 mb-10">
          As an artist, and a stroy teller what moves me is the power of
          connection and the role it plays in uplifiting the community.
        </div>
        <div className="line-4 mb-10">
          What excites me is the opportunity to pair my background in story
          telling with my technical skillset to create meaningul software.
        </div>
      </div>
    );
    return;
  }
  if (tab !== "projects") {
    return;
  }
  setIsHome(false);
  setIsAbout(false);
  setIsProjects(true);
  setContent(<div>projects</div>);
};
