export const tabControl = (tab, setIsHome, setIsAbout, setIsProjects, setContent) => {
  const tabs = {
    home: {
      isHome: true,
      isAbout: false,
      isProjects: false,
      content: (
        <div className="flex flex-col not-fancy-font responsive-text">
          <div className="text-hot-pink">HOME</div>
          <div className="line-1">Hi, I'm Nika!</div>
          <div className="line-2">
            a software engineer and an artist based in Dallas.
          </div>
          <div className="line-3">let's create, collaborate, and connect!</div>
        </div>
      )
    },
    about: {
      isHome: false,
      isAbout: true,
      isProjects: false,
      content: (
        <div className="flex flex-col not-fancy-font  responsive-text " style={{maxWidth: "200px"}}>
          <div className="text-hot-pink">ABOUT</div>
          <div className="line-1">I am a highly experienced software engineer with a passion for creating innovative and user-friendly digital solutions.</div>
          <div className="line-2">With a background in Javascript, HTML5, CSS3, Node.js, and Ruby, I am well-versed in front-end development and an expert in frameworks such as React.</div>
          <div className="line-3">As an artist and a story teller, what moves me is the power of connection and the role it plays in uplifting the community.</div>
          <div className="line-4">What excites me is the opportunity to pair my background in story telling with my technical skillset to create meaningful software.</div>
        </div>
      )
    },
    projects: {
      isHome: false,
      isAbout: false,
      isProjects: true,
      content: <div>projects</div>
    }
  };

  if (tab in tabs) {
    setIsHome(tabs[tab].isHome);
    setIsAbout(tabs[tab].isAbout);
    setIsProjects(tabs[tab].isProjects);
    setContent(tabs[tab].content);
  }
};
