import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars } from "@fortawesome/free-solid-svg-icons";

const MobileNavBar = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div className={`not-fancy-font  ml-10 flex flex-col justify-between text-white text-20 ${menuOpen ? 'menu-open' : ''}`}>
      <button className="text-white hamburger-button" onClick={() => toggleMenu()}>
        <FontAwesomeIcon icon={faBars} className="text-white text-x-large" />
      </button>
      {menuOpen && (
        <div className="flex flex-col gap-5 nav-items">
          <div>About me</div>
          <div>Projects</div>
          <div>Contact</div>
         </div>
      )}
    </div>
  );
};

export default MobileNavBar;
