import React, { useState} from "react";
import LandingPage from "./LandingPage";
import Footer from "./Footer";
import { Route, Routes } from "react-router-dom";
import Header from "./Header";
import NavBar from "./Header/NavBar";

function App() {

  const [isHome, setIsHome] = useState(true);
  const [isAbout, setIsAbout] = useState(false);
  const [isProjects, setIsProjects] = useState(false);

  return (
    <div>
      <Header />
      <NavBar />
      <div className="ml-8 mt-20 mobile-landing">
        <LandingPage
          content={
            <div className="flex flex-col not-fancy-font text-white responsive-text">
              <div className="line-1">Hi, I'm Nika!</div>
              <div className="line-2">
                a software engineer and an artist based in Dallas.
              </div>
              <div className="line-3">
                let's create, collaborate, and connect!
              </div>
            </div>
          }
        />
      </div>
      <Routes>
        {/* <Route exact path="/home" element={<LandingPage />} /> */}
      </Routes>
      <Footer />
    </div>
  );
}

export default App;
