import React, { useState } from "react";
import { Link } from "react-router-dom";
import Clouds from "./Clouds";
import ContactForm from "../ContactForm";

const LandingPage = () => {
  const [showContactForm, setShowContactForm] = useState(false);

  return (
    <div className="landing-page-font flex flex-col justify-center items-center flex-10">
      <div className="text-hot-red max-h-screen  inset-0 flex flex-col items-center justify-center gap-10">
        <Clouds numberOfClouds={10} />
        <Link to="/">
          <h1 className="text-6xl text-sm:text-4xl animate__animated animate__zoomIn">
            Nika Darab
          </h1>
        </Link>
        <Link to="/software-engineer">
          <h2 className="text-4xl text-sm:text-2xl menu-option1">
            Software Engineer
          </h2>
        </Link>
        <Link to="/story-teller">
          <h2 className="text-4xl text-sm:text-2xl menu-option2">
            Story Teller
          </h2>
        </Link>
      </div>
      <div className="flex gap-10 items-center justify-center">
        <a href="https://calendly.com/nikadarab/15min" target="_">
          <h2 className="text-hot-red text-3xl text-sm:text-2xl"> Calendly </h2>
        </a>
        <button>
          <h2
            className="text-hot-red text-3xl text-sm:text-2xl"
            onClick={() => setShowContactForm(true)}
          >
            Contact
          </h2>
        </button>
      </div>
    </div>
  );
};
export default LandingPage;
