import React from "react";
import { PROJECTS } from "./projects";

const Projects = () => (
  <div className="relative overflow-hidden">
    <div className="absolute inset-0">
      {PROJECTS.map((project, index) => (
        <div
          key={project.name}
          className={`bg-white h-screen ${
            index !== 0 ? "hidden" : ""
          }`}
        >
          <div className="container mx-auto py-16">
            <h2 className="text-4xl font-medium text-center">{project.name}</h2>
            <h3 className="text-sm text-gray-700 text-center">{project.techStack}</h3>
            <p className="text-gray-700 mt-2 mx-auto w-3/4 text-center">{project.description}</p>
            <div className="flex justify-center mt-4">
              <a href={project.github} target="_blank" rel="noopener noreferrer" className="btn btn-primary mr-2">Github</a>
              <a href={project.live} target="_blank" rel="noopener noreferrer" className="btn btn-primary">Live App</a>
            </div>
            <img src={project.image} alt={project.alt} className="w-full rounded-lg mx-auto mt-8" />
          </div>
          {index !== PROJECTS.length - 1 && (
            <button
              className="btn btn-primary absolute bottom-0 right-0 mb-4 mr-4"
              onClick={() => {
                const projectElements = document.querySelectorAll(
                  ".project-page"
                );
                projectElements.forEach((el) => el.classList.add("hidden"));
                projectElements[index + 1].classList.remove("hidden");
              }}
            >
              Next Project
            </button>
          )}
        </div>
      ))}
    </div>
  </div>
);

export default Projects;
