export const tabControl = (
  tab,
  setIsHome,
  setIsAbout,
  setIsProjects,
  setContent
) => {
  if (tab === "home") {
    setIsHome(true);
    setIsAbout(false);
    setIsProjects(false);
    setContent(
      <div className="flex flex-col not-fancy-font text-white responsive-text">
        <div className="line-1">Hi, I'm Nika!</div>
        <div className="line-2">
          a software engineer and an artist based in Dallas.
        </div>
        <div className="line-3">let's create, collaborate, and connect!</div>
      </div>
    );
    return;
  }
  if (tab === "about") {
    setIsHome(false);
    setIsAbout(true);
    setIsProjects(false);
    setContent(<div>about</div>);
    return;
  }
  if (tab !== "projects") {
    return;
  }
  setIsHome(false);
  setIsAbout(false);
  setIsProjects(true);
  setContent(<div>projects</div>);
};
