import React, { useEffect } from 'react';

const Petals =()=> {
  useEffect(() => {
    // Create an array of petal images
    const petals = [
        'https://s3-us-west-2.amazonaws.com/s.cdpn.io/641/petal_1.png',
    ];

    // Set up an interval to create new petals every 100ms
    const intervalId = setInterval(() => {
      // Choose a random petal image
      const petalImage = petals[Math.floor(Math.random() * petals.length)];

      for(let i=0; i<100; i++) {
        // Create a new petal element
        const petal = document.createElement('img');
        petal.src = petalImage;
        petal.style.position = 'absolute';
        petal.style.left = `${Math.random() * window.innerWidth}px`;
        petal.style.top = '-50px';

        // Add the petal to the page
        document.body.appendChild(petal);

        // Animate the petal 
        requestAnimationFrame(function animate() {
          petal.style.top = `${petal.getBoundingClientRect().top + 1}px`;
          if (petal.getBoundingClientRect().top < window.innerHeight) {
            requestAnimationFrame(animate);
          } else {
            petal.remove();
          }
        });
      }

    }, 100);

    // Return a cleanup function to remove the interval when the component unmounts
    return () => clearInterval(intervalId);
  }, []);
}

export default Petals;
