import React, { useState } from "react";
import { Link } from "react-router-dom";
import Clouds from "./Clouds";
import { quoteRandomizer } from "../quotes";
import ContactForm from "../ContactForm";
import Typewriter from "typewriter-effect";

const LandingPage = () => {
  const [showContactForm, setShowContactForm] = useState(false);
  const QUOTE = quoteRandomizer();

  return (
    <div className="landing-page-font flex flex-col gap-20 ">

      <div className="flex flex-col items-center justify-center gap-10">
        <div className="text-3xl text-sm:text-2xl">

          <Typewriter
            options={{
              strings: QUOTE.text,
              autoStart: true,

          }}

                  />   

        </div>
      </div>

      <div className="flex items-center justify-center gap-20">
        <Link to="/about-me">
          <h2 className="text-hot-pink text-6xl text-sm:text-xl">About Me</h2>
        </Link>
        <a href="https://calendly.com/nikadarab/15min" target="_">
          <h2 className="text-hot-pink text-6xl text-sm:text-xl">Calendly</h2>
        </a>
        <button>
          <h2
            className="text-hot-pink text-6xl text-sm:text-xl"
            onClick={() => setShowContactForm(true)}
          >
            Contact Me
          </h2>
        </button>
      </div>
      <div className="text-hot-red max-h-screen inset-0 flex flex-col items-center justify-around gap-10">
        <Clouds numberOfClouds={10} />
        <Link to="/">
          <h1 className="text-6xl text-sm:text-4xl animate__animated animate__zoomIn">
            Nika Darab
          </h1>
        </Link>
        <Link to="/software-engineer">
          <h2 className="text-4xl text-sm:text-2xl menu-option1">
            Software Engineer
          </h2>
        </Link>
        <Link to="/story-teller">
          <h2 className="text-4xl text-sm:text-2xl menu-option2">
            Story Teller
          </h2>
        </Link>
      </div>
    </div>
  );
};
export default LandingPage;
