import React, { useState, useEffect } from "react";
import LandingPage from "./LandingPage";
import Footer from "./Footer";
import { Route, Routes } from "react-router-dom";
import Header from "./Header";
import NavBar from "./Header/NavBar";

function App() {
  const [isHome, setIsHome] = useState(true);
  const [isAbout, setIsAbout] = useState(false);
  const [isProjects, setIsProjects] = useState(false);
  const [darkMode, setDarkMode] = useState(true);

  const [content, setContent] = useState(null);

  useEffect(() => {
    const body = document.querySelector("body");
    if (darkMode) {
      body.classList.add("dark-mode");
      body.classList.remove("light-mode");
    } else {
      body.classList.add("light-mode");
      body.classList.remove("dark-mode");
    }
  }, [darkMode]);

  // useEffect(() => {
  //   const mobileNav = document.querySelector(".mobile-nav");
  //   if (darkMode) {
  //     mobileNav.classList.add("light-mode");
  //     mobileNav.classList.remove("dark-mode");
  //   } else {
  //     mobileNav.classList.add("dark-mode");
  //     mobileNav.classList.remove("light-mode");
  //   }
  // }, [darkMode]);

  if (content === null) {
    setContent(
      <div className="flex flex-col not-fancy-font  responsive-text w-full">
        <h5 className="text-hot-pink">HOME</h5>
        <div className="line-1">Hi, I'm Nika!</div>
        <div className="line-2">
          a software engineer and an artist based in Dallas.
        </div>
        <div className="line-3">let's create, collaborate, and connect!</div>
      </div>
    );
  }

  return (
    <div>
      <Header darkMode={darkMode} setDarkMode={setDarkMode} />
      <NavBar
        setIsHome={setIsHome}
        setIsAbout={setIsAbout}
        setIsProjects={setIsProjects}
        content={content}
        setContent={setContent}
      />
      <div className="ml-8 mt-10 mb-20 mobile-landing">
        <LandingPage content={content} />
      </div>
      <Footer
        setIsHome={setIsHome}
        setIsAbout={setIsAbout}
        setIsProjects={setIsProjects}
        content={content}
        setContent={setContent}
      />
      <Routes>
        <Route exact path="/" component={<LandingPage content={content} />} />
        <Route path="/home" component={<LandingPage content={content} />} />
      </Routes>
    </div>
  );
}

export default App;
