import React, { useState } from "react";
import { Link } from "react-router-dom";
import Clouds from "./Clouds";
import 'animate.css';

const LandingPage = () => {
  const [isHovered, setIsHovered] = useState(false);
  const className = isHovered ? 'animate__animated animate__bounce' : '';

  return (
    <div className="text-hot-red max-h-screen absolute inset-0 flex flex-col items-center justify-center gap-10">
      <Clouds numberOfClouds={10}/>
      <Link to="/" 
        className={className}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        <h1 className="text-6xl text-sm:text-4xl">Nika Darab</h1>
      </Link>
      <h2 className="text-4xl text-sm:text-2xl">Software Engineer</h2>
      <h2 className="text-4xl text-sm:text-2xl">Story Teller</h2>
    </div>
  );
}
export default LandingPage;
