import React from "react";
import { useForm, ValidationError } from "@formspree/react";

const ContactForm = () => {
  const [state, handleSubmit] = useForm("mleaanpw");
  if (state.succeeded) {
    return <p>Thanks for joining!</p>;
  }
  return (
    <form onSubmit={handleSubmit} className="p-6 rounded-lg">
    <label htmlFor="email" className="block font-medium mb-2">Email Address</label>
    <input id="email" type="email" name="email" className="w-full p-2 rounded-lg" />
    <ValidationError prefix="Email" field="email" errors={state.errors} className="text-red-500 text-sm" />
    <label htmlFor="message" className="block font-medium   mt-4 mb-2">Message</label>
    <textarea id="message" name="message" className="w-full h-32 border p-2 rounded-lg" />
    <ValidationError prefix="Message" field="message" errors={state.errors} className="text-red-500 text-sm" />
    <button type="submit" disabled={state.submitting} className="bg-indigo-500 text-white py-2 px-4 rounded-lg hover:bg-indigo-600 mt-4">
      Submit
    </button>
  </form>
  );
};
export default ContactForm;
