import React, { useState } from "react";
// import TakeMeHome from "../TakeMeHome";
import PDF from "../resume.pdf";
import Modal from "../Modal";

const SoftwareEngineer = () => {
  const [displayPdf, setDisplayPdf] = useState(false);
  const [selectedOption, setSelectedOption] = useState("Introduction");
  const [toggleIntroduction, setToggleIntroduction] = useState(false);

  const handleIntroduction = () => {
    setToggleIntroduction(!toggleIntroduction);
    setSelectedOption("Introduction");
  };

  const [data] = useState({
    Introduction:
      "Sed porttitor lectus nibh. Vestibulum ac diam sit amet quam vehicula elementum sed sit amet dui. Donec rutrum congue leo eget malesuada. Curabitur arcu erat, accumsan id imperdiet et, porttitor at sem. Quisque velit nisi, pretium ut lacinia in, elementum id enim. Sed porttitor lectus nibh. Proin eget tortor risus. Curabitur arcu erat, accumsan id imperdiet et, porttitor at sem. Nulla quis lorem ut libero malesuada feugiat. Nulla porttitor accumsan tincidunt. Curabitur non nulla sit amet nisl tempus convallis quis ac lectus. Vivamus magna justo, lacinia eget consectetur sed, convallis at tellus. Vivamus suscipit tortor eget felis porttitor volutpat. Proin eget tortor risus. Proin eget tortor risus. Nulla quis lorem ut libero malesuada feugiat. Praesent sapien massa, convallis a pellentesque nec, egestas non nisi. Pellentesque in ipsum id orci porta dapibus. Vivamus magna justo, lacinia eget consectetur sed, convallis at tellus.",
    "The Code I live By": "This is the code data",
    Works: "This is the works data",
  });

  const isMobile = window.matchMedia(
    "only screen and (max-width: 760px)"
  ).matches;

  return (
    <div className="flex justify-center">
      <div className="text-hot-red max-h-screen inset-0 flex flex-col gap-10 swe-page-font items-start justify-center ml-10">
        <button
          className="border-none bg-transparent text-4xl text-sm:text-2xl"
          onClick={handleIntroduction}
        >
          Introduction
        </button>
        <button
          className="border-none bg-transparent text-4xl text-sm:text-2xl"
          onClick={() => setSelectedOption("code")}
        >
          The Code I live By
        </button>
        <button
          className="border-none bg-transparent text-4xl text-sm:text-2xl"
          onClick={() => setSelectedOption("works")}
        >
          Works
        </button>
        {isMobile ? (
          <a href={PDF} download target="_blank" rel="noreferrer">
            <button>Resume on mobile</button>
          </a>
        ) : (
          <button>
            <h2
              className="text-4xl text-sm:text-2xl"
              onClick={() => setDisplayPdf(true)}
            >
              Resume
            </h2>
          </button>
        )}
        {displayPdf && (
          <Modal
            showModal={displayPdf}
            setShowModal={setDisplayPdf}
            content={
              <embed
                src={PDF}
                type="application/pdf"
                width="1000px"
                height="500px"
              />
            }
          />
        )}
      </div>

      <div>
        {toggleIntroduction &&
          (isMobile ? (
            <Modal
              showModal={toggleIntroduction}
              setShowModal={setToggleIntroduction}
              content={data[selectedOption]}
            />
          ) : (
            <div className="reponse">{data[selectedOption]}</div>
          ))}

        {selectedOption === "code" && <div>Code content here</div>}
        {selectedOption === "works" && <div>Works content here</div>}
      </div>
    </div>
  );
};

export default SoftwareEngineer;
