import React from "react";

const Petals = ({ numberOfPetals }) => {
    const petals = [];
    for (let i = 1; i <= numberOfPetals; i++) {
      petals.push(
        <div 
          key={i} 
          className={`absolute petal-${i} top-0 left-0 transform -translate-y-full animation-petal`} 
          style={{animationDuration:`${i/10}s`}}
        />
      );
    }
    return (
      <div className="relative">
        {petals}
      </div>
    );
  };


export default Petals;
