import React from "react";
import { PROJECTS } from "./projects";

const Project = ({ index }) => {
  const selectedProject = PROJECTS[index];
  return (
    <div className="responsive-text not-fancy-font flex flex-col gap-5">
      <div className="flex flex-col gap-3">
        <a
          target="_blank"
          href={selectedProject.live}
          className="animate__animated animate__fadeInDown text-lg"
          rel="noreferrer"
        >
          {selectedProject.name}
        </a>

        <div className="text-m">{selectedProject.description}</div>
      </div>
      <div className="mb-20 flex flex-col gap-3">
        <a href={selectedProject.live} target="_blank" rel="noreferrer">
          <img src={selectedProject.image} alt={selectedProject.name} />
        </a>
         <div className="flex">
          <div className="text-md mr-2">Stack: </div>
          <div className="text-m items-center flex justify-center">{selectedProject.techStack}</div>
         </div>
      </div>
    </div>
  );
};

export default Project;
