import React from "react";
import LandingPage from "../../LandingPage";
import { Link } from "react-router-dom";

const NavBar = ({isHome, setIsHome, isAbout, setIsAbout, isProjects, setIsProjects, setContent }) => {

  const tabControl = (tab) => {
    if (tab === "home") {
      setIsHome(true);
      setIsAbout(false);
      setIsProjects(false);
      setContent(<div className="flex flex-col not-fancy-font text-white responsive-text">
      <div className="line-1">Hi, I'm Nika!</div>
      <div className="line-2">
        a software engineer and an artist based in Dallas.
      </div>
      <div className="line-3">
        let's create, collaborate, and connect!
      </div>
    </div>)
      return;
    }
    if (tab === "about") {
      setIsHome(false);
      setIsAbout(true);
      setIsProjects(false);
      setContent(<div>about</div>)
      return;
    }
    if (tab !== "projects") {
      return;
    }
    setIsHome(false);
    setIsAbout(false);
    setIsProjects(true);
    setContent(<div>projects</div>)
  }


  return (
  <div className="flex justify-between not-fancy-font text-white navbar_fullsize text-25 mt-20 gap-10 w-4/5">
    <div className="flex flex-col ml-8 gap-5 w-800">
      <Link to="/home">
       <button className="border-none bg-transparent" onClick={()=> tabControl("home")}>
       Home
        </button> 
        </Link>
      <div>About</div>
      <div>Projects</div>
    </div>
    <div>
      <LandingPage content={<div className="flex flex-col not-fancy-font text-white responsive-text">
    <div className="line-1">Hi, I'm Nika!</div>
    <div className="line-2">
      a software engineer and an artist based in Dallas.
    </div>
    <div className="line-3">let's create, collaborate, and connect!</div>
  </div>}/>
    </div>
  </div>
)};

export default NavBar;
