export const tabControl = (
  tab,
  setIsHome,
  setIsAbout,
  setIsProjects,
  setContent
) => {
  if (tab === "home") {
    setIsHome(true);
    setIsAbout(false);
    setIsProjects(false);
    setContent(
      <div className="flex flex-col not-fancy-font  responsive-text w-full">
        <h5 className="text-hot-pink">HOME</h5>
        <div className="line-1">Hi, I'm Nika!</div>
        <div className="line-2">
          a software engineer and an artist based in Dallas.
        </div>
        <div className="line-3">let's create, collaborate, and connect!</div>
      </div>
    );
    return;
  }
  if (tab === "about") {
    setIsHome(false);
    setIsAbout(true);
    setIsProjects(false);
    setContent(
      <div className="flex flex-col not-fancy-font  responsive-text mb-10 w-full mr-20">
        <h5 className="text-hot-pink">ABOUT</h5>
        <div className="line-1 mb-10">
          As a software engineer, I am passionate about building applications
          that are user-friendly and intuitive . I am a quick learner and a team
          player.
        </div>

        <div className="line-2 mb-10">
          I am focused on effective problem solving and communication.
        </div>
        <div className="line-3 mb-10">
          As an artist, and a stroy teller what moves me is the power of
          connection. I believe in the power of stories that connects humanity.
        </div>
        <div className="line-4 mb-10">
          My goal is to leave a dent and make a difference. I'm excited to pair
          the power of story telling to develope meaningful software.
        </div>
      </div>
    );
    return;
  }
  if (tab !== "projects") {
    return;
  }
  setIsHome(false);
  setIsAbout(false);
  setIsProjects(true);
  setContent(<div>projects</div>);
};
