import React, { useState } from "react";
import TakeMeHome from "../TakeMeHome";
import PDF from "../resume.pdf";
import Modal from "../Modal";

const SoftwareEngineer = () => {
  const [displayPdf, setDisplayPdf] = useState(false);
  const [selectedOption, setSelectedOption] = useState("Introduction");
  const [toggleIntroduction, setToggleIntroduction] = useState(false);

  const handleIntroduction = () => {
    setToggleIntroduction(!toggleIntroduction);
    setSelectedOption("Introduction");
  };

  const [data, setData] = useState({
    Introduction: "This is the introduction data",
    "The Code I live By": "This is the code data",
    Works: "This is the works data",
  });

  const isMobile = window.matchMedia(
    "only screen and (max-width: 760px)"
  ).matches;

  return (
    <div className="text-hot-red max-h-screen absolute inset-0 flex flex-col gap-10 swe-page-font items-start justify-center ml-10">
      <button
        className="border-none bg-transparent text-4xl text-sm:text-2xl"
        onClick={handleIntroduction}
      >
        Introduction
      </button>
      <button
        className="border-none bg-transparent text-4xl text-sm:text-2xl"
        onClick={() => setSelectedOption("code")}
      >
        The Code I live By
      </button>
      <button
        className="border-none bg-transparent text-4xl text-sm:text-2xl"
        onClick={() => setSelectedOption("works")}
      >
        Works
      </button>
      {isMobile ? (
        <a href={PDF} download target="_blank" rel="noreferrer">
          <button>Resume on mobile</button>
        </a>
      ) : (
        <button>
          <h2
            className="text-4xl text-sm:text-2xl"
            onClick={() => setDisplayPdf(true)}
          >
            Resume
          </h2>
        </button>
      )}
      {displayPdf && (
        <Modal
          showModal={displayPdf}
          setShowModal={setDisplayPdf}
          content={
            <embed
              src={PDF}
              type="application/pdf"
              width="1000px"
              height="500px"
            />
          }
        />
      )}
      {/* {selectedOption === "introduction" && ( */}
        {/* isMobile ?( */}
          <Modal
            showModal={true}
            setShowModal={setSelectedOption}
            content={data[selectedOption]}
            width="w-500"

          />
        {/* ) : */}
        {/* <div>Introduction content here</div> */}
      {/* )} */}
      {selectedOption === "code" && <div>Code content here</div>}
      {selectedOption === "works" && <div>Works content here</div>}
    </div>
  );
};

export default SoftwareEngineer;
